import React, { useState } from 'react';

interface RegistrationModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const RegistrationModal: React.FC<RegistrationModalProps> = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    countryCode: '+1'
  });

  const [selectedCountry, setSelectedCountry] = useState({
    code: 'US',
    dialCode: '+1',
    flag: 'ðŸ‡ºðŸ‡¸'
  });

  const countries = [
    { code: 'US', dialCode: '+1', flag: 'ðŸ‡ºðŸ‡¸', name: 'United States' },
    { code: 'CA', dialCode: '+1', flag: 'ðŸ‡¨ðŸ‡¦', name: 'Canada' },
    { code: 'GB', dialCode: '+44', flag: 'ðŸ‡¬ðŸ‡§', name: 'United Kingdom' },
    { code: 'IN', dialCode: '+91', flag: 'ðŸ‡®ðŸ‡³', name: 'India' },
    { code: 'AU', dialCode: '+61', flag: 'ðŸ‡¦ðŸ‡º', name: 'Australia' },
    { code: 'DE', dialCode: '+49', flag: 'ðŸ‡©ðŸ‡ª', name: 'Germany' },
    { code: 'FR', dialCode: '+33', flag: 'ðŸ‡«ðŸ‡·', name: 'France' },
    { code: 'SG', dialCode: '+65', flag: 'ðŸ‡¸ðŸ‡¬', name: 'Singapore' }
  ];

  const [showCountryDropdown, setShowCountryDropdown] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCountrySelect = (country: typeof countries[0]) => {
    setSelectedCountry(country);
    setFormData(prev => ({
      ...prev,
      countryCode: country.dialCode
    }));
    setShowCountryDropdown(false);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Handle form submission
    console.log('Form submitted:', formData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-3xl w-full max-w-2xl p-8 relative shadow-2xl border-4 border-blue-500">
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-6 right-6 w-10 h-10 bg-gray-100 hover:bg-gray-200 rounded-full flex items-center justify-center transition-colors"
        >
          <svg className="w-5 h-5 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>

        {/* Header */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-4 flex items-center">
            <span className="mr-3 text-xl">ðŸš€</span>
            Pass Your PMP Exam in 30 Days or Less!
          </h2>
          <p className="text-gray-600 text-base">
            Learn the proven 30-Day strategy, Exam Hacks, & Real-World Frameworks in this{' '}
            <span className="font-semibold text-gray-900">FREE PMP Exam Prep Masterclass</span>
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* First Name and Last Name */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                First Name
              </label>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                placeholder="Jane"
                className="w-full px-3 py-3 bg-gray-50 border-0 rounded-xl text-sm text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                Last Name
              </label>
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                placeholder="Smith"
                className="w-full px-3 py-3 bg-gray-50 border-0 rounded-xl text-sm text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
                required
              />
            </div>
          </div>

          {/* Email and Phone Number */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                Email
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="smith23@gmail.com"
                className="w-full px-3 py-3 bg-gray-50 border-0 rounded-xl text-sm text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                Phone Number
              </label>
              <div className="relative">
                <div className="flex bg-gray-50 rounded-xl focus-within:bg-white focus-within:ring-2 focus-within:ring-blue-500 transition-all overflow-hidden">
                  {/* Country code dropdown */}
                  <div className="relative flex-shrink-0">
                    <button
                      type="button"
                      onClick={() => setShowCountryDropdown(!showCountryDropdown)}
                      className="flex items-center px-3 py-3 text-gray-700 hover:bg-gray-100 focus:outline-none transition-colors border-r border-gray-200 min-w-0"
                    >
                      <span className="text-base mr-1.5">{selectedCountry.flag}</span>
                      <span className="text-xs font-medium whitespace-nowrap">{selectedCountry.dialCode}</span>
                      <svg className="w-3 h-3 ml-1.5 text-gray-500 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                      </svg>
                    </button>

                    {/* Country dropdown */}
                    {showCountryDropdown && (
                      <div className="absolute top-full left-0 mt-1 bg-white border border-gray-200 rounded-lg shadow-lg z-10 w-64 max-h-64 overflow-y-auto">
                        {countries.map((country) => (
                          <button
                            key={country.code}
                            type="button"
                            onClick={() => handleCountrySelect(country)}
                            className="w-full flex items-center px-4 py-3 text-left hover:bg-gray-50 transition-colors"
                          >
                            <span className="text-base mr-3">{country.flag}</span>
                            <span className="text-xs font-medium mr-2">{country.dialCode}</span>
                            <span className="text-xs text-gray-600">{country.name}</span>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>

                  {/* Phone input */}
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    placeholder="555123456"
                    className="flex-1 px-3 py-3 bg-transparent text-sm text-gray-900 placeholder-gray-500 focus:outline-none min-w-0"
                    required
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Submit button */}
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold text-base py-3 px-6 rounded-xl transition-all duration-300 transform hover:scale-[1.02] shadow-lg hover:shadow-xl"
          >
            Register Now For Free
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegistrationModal;
