import React from 'react';

const FutureProofSection: React.FC = () => {
  return (
    <section className="bg-gradient-to-br from-purple-700 via-purple-600 to-purple-800 py-20 relative overflow-hidden">
      {/* Purple Noise pattern overlay */}
      <div 
        className="absolute inset-0 opacity-30 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='purpleNoiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.85' numOctaves='3' stitchTiles='stitch'/%3E%3CfeColorMatrix in='turbulence' type='saturate' values='0'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23purpleNoiseFilter)' opacity='1'/%3E%3C/svg%3E")`,
          backgroundSize: '200px 200px'
        }}
      ></div>

      <div className="max-w-6xl mx-auto px-6 relative z-10">
        <div className="text-center">
          {/* Main Heading */}
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-normal text-white mb-6 leading-tight max-w-5xl mx-auto">
            2+ Hours of Live AI Training That Will Transform Your Project Management Skills!
          </h1>

          {/* Subheading */}
          <p className="text-xl md:text-2xl text-purple-100 mb-12 max-w-3xl mx-auto">
            Master tools like Notion AI, ChatGPT & lead smarter projects.
          </p>

          {/* CTA Button */}
          <div className="inline-block">
            <button className="bg-white hover:bg-gray-50 text-gray-900 font-semibold text-lg md:text-xl px-12 py-4 rounded-2xl transition-all duration-300 transform hover:scale-105 shadow-xl">
              Become an AI-Smart Project Manager
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default FutureProofSection;
