import React, { useState, useEffect } from 'react';

interface TimeLeft {
  days: number;
  hours: number;
  minutes: number;
  seconds: number;
}

interface HeroSectionProps {
  openModal: () => void;
}

const HeroSection: React.FC<HeroSectionProps> = ({ openModal }) => {
  const [timeLeft, setTimeLeft] = useState<TimeLeft>({ days: 0, hours: 0, minutes: 0, seconds: 0 });
  const [showStickyBar, setShowStickyBar] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [scrollProgress, setScrollProgress] = useState(0);
  const [urgencyMessage, setUrgencyMessage] = useState("");

  useEffect(() => {
    const targetDate = new Date();
    targetDate.setDate(targetDate.getDate() + 1);
    targetDate.setHours(targetDate.getHours() + 1);
    targetDate.setMinutes(targetDate.getMinutes() + 45);
    targetDate.setSeconds(10);

    const timer = setInterval(() => {
      const now = new Date().getTime();
      const distance = targetDate.getTime() - now;

      if (distance > 0) {
        setTimeLeft({
          days: Math.floor(distance / (1000 * 60 * 60 * 24)),
          hours: Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((distance % (1000 * 60)) / 1000)
        });
      } else {
        setTimeLeft({ days: 0, hours: 0, minutes: 0, seconds: 0 });
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const documentHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = Math.min((currentScrollY / documentHeight) * 100, 100);

      setScrollProgress(progress);

      const secondFoldElement = document.querySelector('[data-section="features"]');
      const scrollingDown = currentScrollY > lastScrollY;
      const scrollingUp = currentScrollY < lastScrollY;

      if (secondFoldElement) {
        const rect = secondFoldElement.getBoundingClientRect();
        const shouldShow = rect.top <= window.innerHeight;
        const isFirstFold = currentScrollY < window.innerHeight;

        // Don't show in first fold, and smart behavior for scroll direction
        if (shouldShow && scrollingDown && !isFirstFold) {
          setShowStickyBar(true);
        } else if (scrollingUp || isFirstFold) {
          setShowStickyBar(false);
        }
      }

      setLastScrollY(currentScrollY);
    };

    // Dynamic urgency messaging based on time
    const updateUrgencyMessage = () => {
      const now = new Date();
      const hours = now.getHours();

      if (hours >= 9 && hours < 12) {
        setUrgencyMessage("Morning Special - Register Now!");
      } else if (hours >= 12 && hours < 17) {
        setUrgencyMessage("Afternoon Rush - Limited Seats!");
      } else if (hours >= 17 && hours < 21) {
        setUrgencyMessage("Evening Deadline Approaching!");
      } else {
        setUrgencyMessage("Last Chance - Register Tonight!");
      }
    };

    updateUrgencyMessage();
    const urgencyInterval = setInterval(updateUrgencyMessage, 60000); // Update every minute

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
      clearInterval(urgencyInterval);
    };
  }, [lastScrollY]);

  return (
    <section className="relative min-h-screen bg-black overflow-hidden">
      {/* Film Grain Noise Pattern */}
      <div 
        className="absolute inset-0 opacity-25 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.85' numOctaves='3' stitchTiles='stitch'/%3E%3CfeColorMatrix in='turbulence' type='saturate' values='0'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='1'/%3E%3C/svg%3E")`,
          backgroundSize: '200px 200px'
        }}
      ></div>

      {/* Additional grain texture */}
      <div 
        className="absolute inset-0 opacity-15 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 150 150' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='grain'%3E%3CfeTurbulence type='turbulence' baseFrequency='0.7' numOctaves='2' result='noise'/%3E%3CfeColorMatrix in='noise' type='saturate' values='0'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23grain)' opacity='0.6'/%3E%3C/svg%3E")`,
          backgroundSize: '150px 150px',
          mixBlendMode: 'multiply'
        }}
      ></div>

      {/* Purple Attention Banner */}
      <div className="text-white py-2 relative z-10" style={{backgroundColor: '#8756f8'}}>
        <div className="max-w-7xl mx-auto px-6 text-center">
          <p className="text-xs md:text-sm font-normal flex items-center justify-center gap-2">
            <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
              <path d="M12 2L3.09 8.26L4 21L12 17L20 21L20.91 8.26L12 2Z"/>
            </svg>
            Attention: For Project Managers Who Want To Pass The PMP Exam On Their First Attempt
          </p>
        </div>
      </div>

      {/* Navigation */}
      <nav className="relative z-10 py-10">
        <div className="max-w-7xl mx-auto px-6 flex items-center justify-between">
          <div className="flex items-center gap-3">
            {/* TechAdemy Logo Icon */}
            <div className="flex items-center justify-center">
              <svg width="32" height="32" viewBox="0 0 40 40" className="text-orange-500">
                <circle cx="12" cy="12" r="3" fill="currentColor"/>
                <circle cx="28" cy="12" r="3" fill="currentColor"/>
                <circle cx="20" cy="28" r="3" fill="currentColor"/>
                <line x1="12" y1="12" x2="28" y2="12" stroke="currentColor" strokeWidth="2"/>
                <line x1="12" y1="12" x2="20" y2="28" stroke="currentColor" strokeWidth="2"/>
                <line x1="28" y1="12" x2="20" y2="28" stroke="currentColor" strokeWidth="2"/>
              </svg>
            </div>
            <div className="text-xl font-bold text-white">
              TECH<span className="text-orange-500">ADEMY</span>
            </div>
          </div>

          {/* Customer Count and Avatars */}
          <div className="flex items-center gap-3">
            <span className="text-gray-300 text-sm font-medium">Join 1,000 + other loving customers</span>
            <div className="flex -space-x-2">
              <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-500 to-purple-600 border-2 border-black flex items-center justify-center">
                <span className="text-white text-xs font-semibold">A</span>
              </div>
              <div className="w-8 h-8 rounded-full bg-gradient-to-br from-green-500 to-emerald-600 border-2 border-black flex items-center justify-center">
                <span className="text-white text-xs font-semibold">S</span>
