import React, { useState } from 'react';

interface FAQ {
  question: string;
  answer: string;
}

const FAQSection: React.FC = () => {
  const [openIndex, setOpenIndex] = useState<number | null>(null);

  const faqs: FAQ[] = [
    {
      question: "What if I'm new to AI? Will I be able to follow along?",
      answer: "Absolutely! This masterclass is designed for product managers of all AI experience levels. We start with fundamentals and gradually build up to advanced concepts. You don't need a technical background—just curiosity and willingness to learn."
    },
    {
      question: "How long is the masterclass?",
      answer: "The live masterclass is 3 hours long with interactive sessions and Q&A. You'll also get lifetime access to recordings and bonus content that adds up to 8+ hours of training materials."
    },
    {
      question: "What if I can't attend the live session?",
      answer: "No problem! All registered participants get lifetime access to the full recording within 24 hours. The recording includes all slides, resources, and Q&A sections."
    },
    {
      question: "Is there a money-back guarantee?",
      answer: "Yes! We offer a 30-day money-back guarantee. If you're not completely satisfied with the content and value provided, we'll refund your full payment, no questions asked."
    },
    {
      question: "Will I get a certificate?",
      answer: "Yes! All participants who attend the live session or complete the recording will receive a certificate of completion that you can add to your LinkedIn profile."
    },
    {
      question: "What tools and resources are included?",
      answer: "You'll get access to our exclusive AI PM toolkit with 50+ curated AI tools, templates, frameworks, and a private community of 5,000+ AI PMs for networking and ongoing support."
    }
  ];

  const toggleFAQ = (index: number) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section className="py-20 bg-gray-50">
      <div className="max-w-4xl mx-auto px-6">
        <div className="text-center mb-16">
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-6">
            Frequently Asked Questions
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            Everything you need to know about the AI PM Masterclass.
          </p>
        </div>

        <div className="space-y-4">
          {faqs.map((faq, index) => (
            <div 
              key={index}
              className="bg-white rounded-xl border border-gray-200 overflow-hidden"
            >
              <button
                className="w-full px-8 py-6 text-left flex items-center justify-between hover:bg-gray-50 transition-colors duration-200"
                onClick={() => toggleFAQ(index)}
              >
                <span className="text-lg font-semibold text-gray-900 pr-8">
                  {faq.question}
                </span>
                <div className={`flex-shrink-0 transform transition-transform duration-200 ${openIndex === index ? 'rotate-45' : ''}`}>
                  <svg className="w-6 h-6 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                </div>
              </button>

              {openIndex === index && (
                <div className="px-8 pb-6">
                  <p className="text-gray-600 leading-relaxed">
                    {faq.answer}
                  </p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQSection;
