import React, { useState } from 'react';

interface RegistrationModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const RegistrationModal: React.FC<RegistrationModalProps> = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    countryCode: '+1'
  });

  const [errors, setErrors] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: ''
  });

  const [validFields, setValidFields] = useState({
    firstName: false,
    lastName: false,
    email: false,
    phone: false
  });

  const [selectedCountry, setSelectedCountry] = useState({
    code: 'US',
    dialCode: '+1',
    flag: 'ðŸ‡ºðŸ‡¸'
  });

  const countries = [
    { code: 'US', dialCode: '+1', flag: 'ðŸ‡ºðŸ‡¸', name: 'United States' },
    { code: 'CA', dialCode: '+1', flag: 'ðŸ‡¨ðŸ‡¦', name: 'Canada' },
    { code: 'GB', dialCode: '+44', flag: 'ðŸ‡¬ðŸ‡§', name: 'United Kingdom' },
    { code: 'IN', dialCode: '+91', flag: 'ðŸ‡®ðŸ‡³', name: 'India' },
    { code: 'AU', dialCode: '+61', flag: 'ðŸ‡¦ðŸ‡º', name: 'Australia' },
    { code: 'DE', dialCode: '+49', flag: 'ðŸ‡©ðŸ‡ª', name: 'Germany' },
    { code: 'FR', dialCode: '+33', flag: 'ðŸ‡«ðŸ‡·', name: 'France' },
    { code: 'SG', dialCode: '+65', flag: 'ðŸ‡¸ðŸ‡¬', name: 'Singapore' },
    { code: 'JP', dialCode: '+81', flag: 'ðŸ‡¯ðŸ‡µ', name: 'Japan' },
    { code: 'CN', dialCode: '+86', flag: 'ðŸ‡¨ðŸ‡³', name: 'China' },
    { code: 'BR', dialCode: '+55', flag: 'ðŸ‡§ðŸ‡·', name: 'Brazil' },
    { code: 'MX', dialCode: '+52', flag: 'ðŸ‡²ðŸ‡½', name: 'Mexico' },
    { code: 'RU', dialCode: '+7', flag: 'ðŸ‡·ðŸ‡º', name: 'Russia' },
    { code: 'IT', dialCode: '+39', flag: 'ðŸ‡®ðŸ‡¹', name: 'Italy' },
    { code: 'ES', dialCode: '+34', flag: 'ðŸ‡ªðŸ‡¸', name: 'Spain' },
    { code: 'KR', dialCode: '+82', flag: 'ðŸ‡°ðŸ‡·', name: 'South Korea' },
    { code: 'NL', dialCode: '+31', flag: 'ðŸ‡³ðŸ‡±', name: 'Netherlands' },
    { code: 'CH', dialCode: '+41', flag: 'ðŸ‡¨ðŸ‡­', name: 'Switzerland' },
    { code: 'BE', dialCode: '+32', flag: 'ðŸ‡§ðŸ‡ª', name: 'Belgium' },
    { code: 'SE', dialCode: '+46', flag: 'ðŸ‡¸ðŸ‡ª', name: 'Sweden' },
    { code: 'NO', dialCode: '+47', flag: 'ðŸ‡³ðŸ‡´', name: 'Norway' },
    { code: 'DK', dialCode: '+45', flag: 'ðŸ‡©ðŸ‡°', name: 'Denmark' },
    { code: 'FI', dialCode: '+358', flag: 'ðŸ‡«ðŸ‡®', name: 'Finland' },
    { code: 'PL', dialCode: '+48', flag: 'ðŸ‡µðŸ‡±', name: 'Poland' },
    { code: 'AT', dialCode: '+43', flag: 'ðŸ‡¦ðŸ‡¹', name: 'Austria' },
    { code: 'PT', dialCode: '+351', flag: 'ðŸ‡µðŸ‡¹', name: 'Portugal' },
    { code: 'IE', dialCode: '+353', flag: 'ðŸ‡®ðŸ‡ª', name: 'Ireland' },
    { code: 'NZ', dialCode: '+64', flag: 'ðŸ‡³ðŸ‡¿', name: 'New Zealand' },
    { code: 'ZA', dialCode: '+27', flag: 'ðŸ‡¿ðŸ‡¦', name: 'South Africa' },
    { code: 'EG', dialCode: '+20', flag: 'ðŸ‡ªðŸ‡¬', name: 'Egypt' },
    { code: 'NG', dialCode: '+234', flag: 'ðŸ‡³ðŸ‡¬', name: 'Nigeria' },
    { code: 'KE', dialCode: '+254', flag: 'ðŸ‡°ðŸ‡ª', name: 'Kenya' },
    { code: 'GH', dialCode: '+233', flag: 'ðŸ‡¬ðŸ‡­', name: 'Ghana' },
    { code: 'AE', dialCode: '+971', flag: 'ðŸ‡¦ðŸ‡ª', name: 'UAE' },
    { code: 'SA', dialCode: '+966', flag: 'ðŸ‡¸ðŸ‡¦', name: 'Saudi Arabia' },
    { code: 'TR', dialCode: '+90', flag: 'ðŸ‡¹ðŸ‡·', name: 'Turkey' },
    { code: 'IL', dialCode: '+972', flag: 'ðŸ‡®ðŸ‡±', name: 'Israel' },
    { code: 'TH', dialCode: '+66', flag: 'ðŸ‡¹ðŸ‡­', name: 'Thailand' },
    { code: 'MY', dialCode: '+60', flag: 'ðŸ‡²ðŸ‡¾', name: 'Malaysia' },
    { code: 'PH', dialCode: '+63', flag: 'ðŸ‡µðŸ‡­', name: 'Philippines' },
    { code: 'ID', dialCode: '+62', flag: 'ðŸ‡®ðŸ‡©', name: 'Indonesia' },
    { code: 'VN', dialCode: '+84', flag: 'ðŸ‡»ðŸ‡³', name: 'Vietnam' },
    { code: 'BD', dialCode: '+880', flag: 'ðŸ‡§ðŸ‡©', name: 'Bangladesh' },
    { code: 'PK', dialCode: '+92', flag: 'ðŸ‡µðŸ‡°', name: 'Pakistan' },
    { code: 'LK', dialCode: '+94', flag: 'ðŸ‡±ðŸ‡°', name: 'Sri Lanka' },
    { code: 'AR', dialCode: '+54', flag: 'ðŸ‡¦ðŸ‡·', name: 'Argentina' },
    { code: 'CL', dialCode: '+56', flag: 'ðŸ‡¨ðŸ‡±', name: 'Chile' },
    { code: 'CO', dialCode: '+57', flag: 'ðŸ‡¨ðŸ‡´', name: 'Colombia' },
    { code: 'PE', dialCode: '+51', flag: 'ðŸ‡µðŸ‡ª', name: 'Peru' },
    { code: 'VE', dialCode: '+58', flag: 'ðŸ‡»ðŸ‡ª', name: 'Venezuela' }
  ];

  const [showCountryDropdown, setShowCountryDropdown] = useState(false);

  const validateField = (name: string, value: string) => {
    switch (name) {
      case 'firstName':
      case 'lastName':
        if (value.length < 2) return 'Must be at least 2 characters';
        if (!/^[a-zA-Z\s]+$/.test(value)) return 'Only letters and spaces allowed';
        return '';
      case 'email':
        if (!value) return 'Email is required';
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) return 'Invalid email format';
        return '';
      case 'phone':
        if (!value) return 'Phone number is required';
        if (!/^\d+$/.test(value)) return 'Only numbers are allowed';
        if (value.length < 7 || value.length > 15) return 'Phone number must be 7-15 digits';
        return '';
      default:
        return '';
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    // For phone field, only allow numbers
    if (name === 'phone') {
      const numbersOnly = value.replace(/\D/g, '');
      setFormData(prev => ({
        ...prev,
        [name]: numbersOnly
      }));

      // Validate phone field
      const error = validateField(name, numbersOnly);
      setErrors(prev => ({ ...prev, [name]: error }));
      setValidFields(prev => ({ ...prev, [name]: !error && numbersOnly.length > 0 }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));

      // Validate other fields
      const error = validateField(name, value);
      setErrors(prev => ({ ...prev, [name]: error }));
      setValidFields(prev => ({ ...prev, [name]: !error && value.length > 0 }));
    }
  };

  const handleCountrySelect = (country: typeof countries[0]) => {
    setSelectedCountry(country);
    setFormData(prev => ({
      ...prev,
      countryCode: country.dialCode
    }));
    setShowCountryDropdown(false);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Handle form submission
    console.log('Form submitted:', formData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4" onClick={onClose}>
      <div className="bg-white rounded-3xl w-full max-w-2xl p-8 relative shadow-2xl border-4 border-blue-500" onClick={(e) => e.stopPropagation()}>
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-6 right-6 w-10 h-10 bg-gray-100 hover:bg-gray-200 rounded-full flex items-center justify-center transition-colors"
        >
          <svg className="w-5 h-5 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>

        {/* Header */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-4 flex items-center">
            <span className="mr-3 text-xl">ðŸš€</span>
            Pass Your PMP Exam in 30 Days or Less!
          </h2>
          <p className="text-gray-600 text-base">
            Learn the proven 30-Day strategy, Exam Hacks, & Real-World Frameworks in this{' '}
            <span className="font-semibold text-gray-900">FREE PMP Exam Prep Masterclass</span>
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* First Name and Last Name */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                First Name
              </label>
              <div className="relative">
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  placeholder="Jane"
                  className="w-full px-3 py-3 pr-10 bg-gray-50 border-0 rounded-xl text-sm text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
                  required
                />
                {/* Validation Icon */}
                <div className="absolute right-3 top-3 flex items-center">
                  {formData.firstName && (
                    validFields.firstName ? (
                      <svg className="w-5 h-5 text-green-500" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                    ) : (
                      <div className="relative group">
                        <svg className="w-5 h-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                        </svg>
                        {errors.firstName && (
                          <div className="absolute bottom-full mb-2 left-1/2 transform -translate-x-1/2 px-2 py-1 bg-gray-800 text-white text-xs rounded whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity">
                            {errors.firstName}
                          </div>
                        )}
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                Last Name
              </label>
              <div className="relative">
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                  placeholder="Smith"
                  className="w-full px-3 py-3 pr-10 bg-gray-50 border-0 rounded-xl text-sm text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
                  required
                />
                {/* Validation Icon */}
                <div className="absolute right-3 top-3 flex items-center">
                  {formData.lastName && (
                    validFields.lastName ? (
                      <svg className="w-5 h-5 text-green-500" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                    ) : (
                      <div className="relative group">
                        <svg className="w-5 h-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                        </svg>
                        {errors.lastName && (
                          <div className="absolute bottom-full mb-2 left-1/2 transform -translate-x-1/2 px-2 py-1 bg-gray-800 text-white text-xs rounded whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity">
                            {errors.lastName}
                          </div>
                        )}
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Email and Phone Number */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                Email
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="smith23@gmail.com"
                className="w-full px-3 py-3 bg-gray-50 border-0 rounded-xl text-sm text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 text-xs font-medium mb-2">
                Phone Number
              </label>
              <div className="relative">
                <div className="flex bg-gray-50 rounded-xl focus-within:bg-white focus-within:ring-2 focus-within:ring-blue-500 transition-all overflow-hidden">
                  {/* Country code dropdown */}
                  <div className="relative flex-shrink-0">
                    <button
                      type="button"
                      onClick={() => setShowCountryDropdown(!showCountryDropdown)}
                      className="flex items-center px-3 py-3 text-gray-700 hover:bg-gray-100 focus:outline-none transition-colors border-r border-gray-300 min-w-0"
                    >
                      <span className="text-base mr-1.5">{selectedCountry.flag}</span>
                      <span className="text-xs font-medium whitespace-nowrap">{selectedCountry.dialCode}</span>
                      <svg className="w-3 h-3 ml-1.5 text-gray-500 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                      </svg>
                    </button>

                    {/* Country dropdown */}
                    {showCountryDropdown && (
                      <div className="absolute top-full left-0 mt-1 bg-white border border-gray-200 rounded-lg shadow-lg z-20 w-64 max-h-64 overflow-y-auto">
                        {countries.map((country) => (
                          <button
                            key={country.code}
                            type="button"
                            onClick={() => handleCountrySelect(country)}
                            className="w-full flex items-center px-4 py-3 text-left hover:bg-gray-50 transition-colors"
                          >
                            <span className="text-base mr-3">{country.flag}</span>
                            <span className="text-xs font-medium mr-2">{country.dialCode}</span>
                            <span className="text-xs text-gray-600">{country.name}</span>
                          </button>
                        ))}
                      </div>
                    )}
                  </div>

                  {/* Phone input */}
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    placeholder="555123456"
                    className="flex-1 px-3 py-3 bg-transparent text-sm text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-0 border-0 min-w-0"
                    required
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Submit button */}
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold text-base py-3 px-6 rounded-xl transition-all duration-300 transform hover:scale-[1.02] shadow-lg hover:shadow-xl"
          >
            Register Now For Free
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegistrationModal;
