import React from 'react';

interface Testimonial {
  quote: string;
  author: string;
  role: string;
  company: string;
  location: string;
  avatar: string;
}

const TestimonialsSection: React.FC = () => {
  const testimonials: Testimonial[] = [
    {
      quote: "This masterclass completely changed how I approach product strategy. The AI frameworks taught here helped me launch 3 successful features that increased user engagement by 150%.",
      author: "Sarah Johnson",
      role: "Senior PM",
      company: "Netflix",
      location: "USA",
      avatar: "SJ"
    },
    {
      quote: "The hands-on approach and real case studies made complex AI concepts easy to understand and apply. I got promoted to Principal PM within 6 months of completing this course.",
      author: "Michael Kim",
      role: "Principal PM",
      company: "Google",
      location: "Canada",
      avatar: "MK"
    },
    {
      quote: "As a startup founder, this masterclass gave me the strategic insights I needed to position our AI product in the market. We raised our Series A shortly after implementing these strategies.",
      author: "Priya Raj",
      role: "Founder & CEO",
      company: "TechStartup",
      location: "India",
      avatar: "PR"
    },
    {
      quote: "The AI tools and techniques I learned here reduced our product development time by 40%. This masterclass is worth every penny for any PM serious about staying relevant.",
      author: "James Wilson",
      role: "VP Product",
      company: "Microsoft",
      location: "UK",
      avatar: "JW"
    }
  ];

  return (
    <section className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-6">
        <div className="text-center mb-16">
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-6">
            What Our Students Say
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            Join thousands of product managers who've transformed their careers with our AI PM training.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {testimonials.map((testimonial, index) => (
            <div 
              key={index}
              className="bg-gray-50 rounded-2xl p-8 hover:shadow-xl transition-all duration-300"
            >
              <div className="mb-6">
                <svg className="w-10 h-10 text-blue-500 mb-4" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM6.293 6.707a1 1 0 010-1.414l3-3a1 1 0 011.414 0l3 3a1 1 0 01-1.414 1.414L11 5.414V13a1 1 0 11-2 0V5.414L7.707 6.707a1 1 0 01-1.414 0z" clipRule="evenodd" />
                </svg>
              </div>

              <blockquote className="text-lg text-gray-700 leading-relaxed mb-6 italic">
                "{testimonial.quote}"
              </blockquote>

              <div className="flex items-center">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center text-white font-bold mr-4">
                  {testimonial.avatar}
                </div>
                <div>
                  <div className="font-bold text-gray-900">{testimonial.author}</div>
                  <div className="text-sm text-gray-600">
                    {testimonial.role} at {testimonial.company}, {testimonial.location}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TestimonialsSection;
