import React from 'react';

const FutureProofSection: React.FC = () => {
  return (
    <section className="bg-black py-20 relative overflow-hidden">
      {/* Film Grain Noise Pattern - matching other sections */}
      <div 
        className="absolute inset-0 opacity-20 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.85' numOctaves='3' stitchTiles='stitch'/%3E%3CfeColorMatrix in='turbulence' type='saturate' values='0'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='1'/%3E%3C/svg%3E")`,
          backgroundSize: '200px 200px'
        }}
      ></div>

      {/* Content container */}
      <div className="max-w-5xl mx-auto px-6 relative z-10">
        {/* Purple rounded container matching reference */}
        <div className="bg-gradient-to-br from-purple-700 via-purple-600 to-purple-800 rounded-3xl py-16 px-12 relative overflow-hidden">
          {/* Purple Noise pattern overlay */}
          <div 
            className="absolute inset-0 opacity-20 pointer-events-none"
            style={{
              backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='purpleNoiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.85' numOctaves='3' stitchTiles='stitch'/%3E%3CfeColorMatrix in='turbulence' type='saturate' values='0'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23purpleNoiseFilter)' opacity='1'/%3E%3C/svg%3E")`,
              backgroundSize: '200px 200px'
            }}
          ></div>

          <div className="text-center relative z-10">
            {/* Main Heading using design system */}
            <h1 className="hero-heading mb-6 max-w-4xl mx-auto">
              2+ Hours of Live AI Training That Will Transform Your Project Management Skills!
            </h1>

            {/* Subheading using design system */}
            <p className="hero-subheading text-purple-100 mb-12 max-w-3xl mx-auto">
              Master tools like Notion AI, ChatGPT & lead smarter projects.
            </p>

            {/* CTA Button using design system */}
            <div className="inline-block">
              <button className="cta-button">
                Become an AI-Smart Project Manager
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default FutureProofSection;
