import React, { useState, useEffect } from 'react';

interface Testimonial {
  quote: string;
  author: string;
  role: string;
  company: string;
  location: string;
  avatar: string;
  title: string;
}

const TestimonialsSection: React.FC = () => {
  const [offset, setOffset] = useState(0);

  const testimonials: Testimonial[] = [
    {
      title: "Finally, a training provider that actually delivers",
      quote: "Techademy's course on AI for Product Managers provided exactly what I needed - practical frameworks for implementing AI features without getting lost in technical jargon. The case studies were particularly valuable.",
      author: "Brody Jay",
      role: "",
      company: "",
      location: "United States",
      avatar: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=150&q=80"
    },
    {
      title: "The right partner for your online training",
      quote: "As a project manager, I've dealt with too many vendors who promise the world and they don't deliver. But Techademy delivers exactly what they advertise, on time and at a great price for the value delivered at its best.",
      author: "Ben Johnson",
      role: "",
      company: "",
      location: "United States",
      avatar: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=150&q=80"
    },
    {
      title: "Worth every hour and every dollar invested",
      quote: "Great course! Techademy showed me how to use AI tools in my daily project management work. Now I'm using ChatGPT for meeting summaries and other AI tools for scheduling. Its a Very practical approach.",
      author: "Harry",
      role: "",
      company: "",
      location: "United States",
      avatar: "https://images.unsplash.com/photo-1519244703995-f4e0f30006d5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=150&q=80"
    },
    {
      title: "Helped me apply AI to my work the very next day",
      quote: "Very Useful Prompts & Tools. The prompts & tools that you get with the program have been very useful in my day-to-day PM responsibilities. I've been able to generate project briefs, meeting agendas & status reports in a fraction of the time it used to take.",
      author: "Morgan H",
      role: "",
      company: "",
      location: "United States",
      avatar: "https://images.unsplash.com/photo-1494790108755-2616b612b786?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=150&q=80"
    },
    {
      title: "Practical AI Skills I Could Use Immediately",
      quote: "As a traditional PM, I was skeptical about AI but this Techademy course changed my perspective completely. The trainers explained everything in simple terms and gave real examples. I'm now implementing AI solutions in my projects.",
      author: "Beatrice",
      role: "",
      company: "",
      location: "United States",
      avatar: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=150&q=80"
    },
    {
      title: "Outstanding Material for AI Implementation",
      quote: "With limited knowledge on the subject, Techademy's AI for Project Managers course equipped me with everything I needed to confidently spearhead AI implementation projects. Seeing how valuable the risk assessment templates were, made the entire course investment worthwhile.",
      author: "Hendrix Jay",
      role: "",
      company: "",
      location: "United States",
      avatar: "https://images.unsplash.com/photo-1560250097-0b93528c311a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=150&q=80"
    }
  ];

  // Continuous infinite scroll - moves 1px every 50ms
  useEffect(() => {
    const interval = setInterval(() => {
      setOffset((prev) => prev + 1);
    }, 50);

    return () => clearInterval(interval);
  }, []);

  // Triple the testimonials for seamless infinite scroll
  const infiniteTestimonials = [...testimonials, ...testimonials, ...testimonials];

  return (
    <section className="py-20 bg-black relative overflow-hidden">
      {/* Film Grain Noise Pattern - matching other sections */}
      <div 
        className="absolute inset-0 opacity-20 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.85' numOctaves='3' stitchTiles='stitch'/%3E%3CfeColorMatrix in='turbulence' type='saturate' values='0'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='1'/%3E%3C/svg%3E")`,
          backgroundSize: '200px 200px'
        }}
      ></div>

      <div className="max-w-7xl mx-auto px-6 relative z-10">
        <div className="text-center mb-16">
          <div className="inline-flex items-center gap-2 bg-gray-800 text-gray-300 px-4 py-2 rounded-full text-sm mb-6">
            <span className="text-orange-500">✦</span>
            Success Stories
          </div>
          <h2 className="text-3xl font-normal text-white mb-6">
            Hear from Professionals Who've Attended This Session
          </h2>
        </div>

        {/* Infinite Scroll Container */}
        <div className="overflow-hidden">
          <div 
            className="flex gap-8"
            style={{ 
              transform: `translateX(-${offset}px)`,
              width: `${infiniteTestimonials.length * 420}px` // 420px per card (400 + 20 gap)
            }}
          >
            {infiniteTestimonials.map((testimonial, index) => (
              <div 
                key={`${testimonial.author}-${index}`}
                className="w-96 flex-shrink-0"
                style={{
                  // Reset position when reaching end of first set
                  transform: offset > testimonials.length * 420 ? `translateX(${testimonials.length * 420}px)` : 'none'
                }}
              >
                {/* Card exactly matching reference design */}
                <div className="rounded-3xl border border-gray-700 p-8 h-[480px] relative bg-gray-900/20 backdrop-blur-sm">
                  {/* Orange pause icon - exactly like reference */}
                  <div className="absolute top-8 left-8">
                    <div className="flex gap-1">
                      <div className="w-1.5 h-5 bg-orange-500 rounded-full"></div>
                      <div className="w-1.5 h-5 bg-orange-500 rounded-full"></div>
                    </div>
                  </div>

                  {/* Title - large and prominent */}
                  <h3 className="text-3xl font-normal text-white mb-6 mt-16 leading-tight">
                    {testimonial.title}
                  </h3>

                  {/* Quote text - matching reference spacing and color */}
                  <p className="text-gray-400 leading-relaxed mb-16 text-base">
                    {testimonial.quote}
                  </p>

                  {/* Author section - positioned at bottom */}
                  <div className="absolute bottom-8 left-8 flex items-center">
                    <div className="w-16 h-16 rounded-full overflow-hidden mr-4 flex-shrink-0">
                      <img 
                        src={testimonial.avatar}
                        alt={testimonial.author}
                        className="w-full h-full object-cover"
                      />
                    </div>
                    <div>
                      <div className="text-xl font-medium text-white mb-1">
                        {testimonial.author}
                      </div>
                      <div className="text-gray-400 text-sm">
                        {testimonial.location}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default TestimonialsSection;
